import React, { useState } from 'react';
import CustomBtn from '../../Components/CustomeBtn/CustomBtn';
import { Tabs } from '../../Components/StyledComponents/Index';

const NavTab = ({ togglePeopleList,  }) => {
    return (
        <Tabs className='bg-[#00ff15] w-[10%] items-center'>
        <CustomBtn text='Message' onClick={togglePeopleList} />
        <CustomBtn text='Teams' onClick={toggleTeamsLists} />
        </Tabs>
    );
};

const ChatsProfileTabs = ({ showPeopleList }) => {
    return (
        <Tabs className='bg-[green] w-[25%]'>
        <p>Chats Profile</p>
        {showPeopleList && (
            <div>
            <h3>People who messaged:</h3>
            {/* Render the list of people */}
            </div>
        )}
        </Tabs>
    );
};

const ChatsTabs = () => {
    return (
        <Tabs className='bg-[red] w-[50%]'>
        <p>Chats</p>
        </Tabs>
    );
};

const ProfileTab = () => {
    return (
        <Tabs className='bg-[blue] w-[25%]'>
        <p>Profile</p>
        </Tabs>
    );
};

const Main = () => {
    const [showPeopleList, setShowPeopleList] = useState(false);

    const togglePeopleList = () => {
        setShowPeopleList(!showPeopleList);
    };

    return (
        <div className='flex flex-col items-center h-screen'>
        <div className='flex items-center bg-slate-400 w-[75%]'>
            <NavTab togglePeopleList={togglePeopleList} />
            <ChatsProfileTabs showPeopleList={showPeopleList} />
            <ChatsTabs />
            <ProfileTab />
        </div>
        </div>
    );
};

export default Main;
